// import React, { useContext } from 'react';
import PropTypes from 'prop-types';
import Header from '../components/Header';
// import { Link } from 'react-router-dom';
// import shareIcon from '../images/shareIcon.svg';

export default function DoneRecipes({ match: { params: { id } } }) {
  // const [doneRecipeData, setdoneRecipeData] = useState({});
  // const { favRecipe } = useContext(RecipesContext);
  const doneRecipesData = (localStorage.getItem('doneRecipes'));
  // const [urlCopied, setUrlCopied] = useState(false);
  // const shareRecipe = () => {
  //   copy(window.location.href);
  //   setUrlCopied(true);
  // };
  console.log(doneRecipesData);
  return (
    <div>
      <Header />
      <p>{ id }</p>
      <div data-testid="filters">
        <button type="button" data-testid="filter-by-all-btn">All</button>
        <button type="button" data-testid="filter-by-meal-btn">Filter By Meal</button>
        <button type="button" data-testid="filter-by-drink-btn">Filter By Drink</button>
        <div>
          { doneRecipesData.map((recipe, i) => (
            <div key={ recipe }>
              <img
                src={ recipe.strMealThumb }
                alt="card"
                data-testid={ `${i}-horizontal-image` }
              />
              <p data-testid={ `${i}-horizontal-top-text` }>{ recipe.strCategory }</p>
              <p data-testid={ `${i}-horizontal-name` }>{ strMeal }</p>
              <p data-testid={ `${i}-horizontal-done-date` }>Made in</p>
              <button
                type="button"
                data-testid={ `${i}-horizontal-share-btn` }
                onClick={ shareRecipe }
              >
                <img src={ shareIcon } alt="share" />
              </button>
              strTags
              <p data-testid={ `${i}-${tagName}-horizontal-tag` }>Show Tags</p>

            </div>
          ),
          )}
        </div>
      </div>
    </div>
  );
}

DoneRecipes.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.string,
    }),
  }),
}.isRequired;
