import React from 'react';
import copy from 'clipboard-copy';
import shareIcon from '../images/shareIcon.svg';
import RecipesContext from '../hooks/RecipesContext';
import whiteHeartIcon from '../images/whiteHeartIcon.svg';
import blackHeartIcon from '../images/blackHeartIcon.svg';

export default function ButtonsFavorite() {
  const [favoriteIcon, setFavoriteIcon] = useState(whiteHeartIcon);
  const [urlCopied, setUrlCopied] = useState(false);

  const { saveRecipeToLocalStorage,
    removeRecipeFromLocalStorage,
    isRecipeOnLocalStorage } = useContext(RecipesContext);

  const shareRecipe = () => {
    copy(window.location.href);
    setUrlCopied(true);
  };

  const favoriteRecipe = (id) => {
    if (isRecipeOnLocalStorage(id)) {
      removeRecipeFromLocalStorage(id);
    } else {
      saveRecipeToLocalStorage(id);
    }
  };

  useEffect((id) => {
    if (isRecipeOnLocalStorage(id)) setFavoriteIcon(blackHeartIcon);
    else setFavoriteIcon(whiteHeartIcon);
  }, [favoriteIcon]);

  return (
    <div>
      {urlCopied && (<p>Link copied!</p>)}
      <button
        type="button"
        data-testid="share-btn"
        onClick={ shareRecipe }
      >
        <img src={ shareIcon } alt="share" />
      </button>
      <button
        type="button"
        data-testid="favorite-btn"
        onClick={ favoriteRecipe }
      >
        <img src={ favoriteIcon } alt="favorite" />
      </button>
    </div>
  );
}
